angular.module("spring-security-csrf-token-interceptor",[]).config(["$httpProvider",function($httpProvider){var xhr=new XMLHttpRequest;xhr.open("head","/",!1),xhr.send();var csrfToken=xhr.getResponseHeader("X-CSRF-TOKEN");$httpProvider.interceptors.push(function($q){return{request:function(config){return config.headers["X-CSRF-TOKEN"]=csrfToken,config||$q.when(config)}}})}]);